import React, { useState } from 'react';
import PdfLink from './PdfLink'; // Importing PdfLink component

const InputForm = () => {
  const [district, setDistrict] = useState('');
  const [pdfLink, setPdfLink] = useState(null);

  const handleSubmit = (event) => {
    event.preventDefault();
    // Construct the PDF URL based on the district name
    const filename = `${district}_report.pdf`;
    setPdfLink(`/static/pdfs/${filename}`);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          District Name:
          <input
            type="text"
            value={district}
            onChange={(e) => setDistrict(e.target.value)}
            required
          />
        </label>
        <button type="submit">View PDF</button>
      </form>

      {pdfLink && <PdfLink pdfLink={pdfLink} />} {/* Using PdfLink component */}
    </div>
  );
};

export default InputForm;
